import React, { useState } from 'react'

const App = () => {
  const anecdotes = [
    'If it hurts, do it more often.',
    'Adding manpower to a late software project makes it later!',
    'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
    'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
    'Premature optimization is the root of all evil.',
    'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.',
    'Programming without an extremely heavy use of console.log is same as if a doctor would refuse to use x-rays or blood tests when diagnosing patients.'
  ]

  const [selected, setSelected] = useState(0)
  const [votes, setVotes] = useState(new Array(anecdotes.length).fill(0))

  const randomAnecdote = () => setSelected(Math.floor(Math.random() * anecdotes.length))

  const vote = () => {
    const newVotes = [...votes]
    newVotes[selected] += 1
    setVotes(newVotes)
  }

  return (
    <div>
      <h2>Anecdote of the day</h2>
      <Anecdote anecdotes={anecdotes} votes={votes} selected={selected}/>
      <Button text="vote" handleclick={vote}/>
      <Button text="next anecdote" handleclick={randomAnecdote}/>
      <h2>Anecdote with most votes</h2>
      <MostVotedAnecdote anecdotes={anecdotes} votes={votes}/>
    </div>
  )
}

const Button = ({text, handleclick}) => (
  <button onClick={handleclick}>{text}</button>
)

const MostVotedAnecdote = ({anecdotes, votes}) => {
  if (votes.some((anecdote) => anecdote > 0)) {
    const mostVoted = (votes.indexOf(Math.max(...votes)))
    return <Anecdote anecdotes={anecdotes} votes={votes} selected={mostVoted}/>
  }
  return <p>No votes have been cast yet</p>
}

const Anecdote = ({anecdotes, votes, selected}) => (
  <div>
    <p>{anecdotes[selected]}</p>
    <p>has {votes[selected]} votes</p>
  </div>
)

export default App
